-- TASK 1: Bars that ordered more than 5 types of beer
-- customers with ID's 1,2,3 are bars, customer id 20 is a store and should not show in the select

INSERT INTO orders (customer_id, delivery_address)
SELECT t1.customer_id, c.address FROM
    (SELECT
         CASE
             WHEN i < 4 THEN 1
             WHEN i < 7 AND i > 3 THEN 2
             WHEN i < 10 AND i > 6 THEN 3
             ELSE 20
             END as customer_id
     FROM generate_series(1,12) as i) as t1
JOIN customers c ON customer_id = c.id;

INSERT INTO order_items (order_id, product_pack_id, quantity, unit_price)
SELECT
    o_id,
    pp_id,
    floor(random() * 5)::int +1 as quantity,
    unit_price
FROM orders o
    JOIN LATERAL (
        SELECT o.id AS o_id, p.id, pp.id as pp_id, pp.volume * price_per_liter as unit_price
        FROM products p
            JOIN product_packs pp ON p.id = pp.product_id
        ORDER BY random()
        LIMIT 5
        ) AS t1 ON TRUE
WHERE o.customer_id IN (1,2,3,20);

