INSERT INTO order_items (order_id, product_pack_id, quantity, unit_price)
SELECT
    CASE
        WHEN i = 1 THEN 33
        WHEN i = 2 THEN 34
    END as order_id,
    product_pack_id,
    quantity,
    unit_price
FROM generate_series(1,2) as i
